/**
 * Get app configuration by slug
 * @param {string} slug - The app slug (e.g., "invoice-nudge")
 * @returns {object|null} - App config or null if not found
 */
export function getApp(slug) {
  return APPS[slug] || null;
}

/**
 * Get all apps
 * @returns {object[]} - Array of all app configs
 */
export function getAllApps() {
  return Object.values(APPS);
}

/**
 * Register a new app (called by Launchpad during generation)
 * @param {object} appConfig - The app configuration
 */
export function registerApp(appConfig) {
  APPS[appConfig.slug] = {
    ...appConfig,
    createdAt: new Date().toISOString(),
    status: "active",
  };
}

/**
 * Check if an app exists
 * @param {string} slug - The app slug
 * @returns {boolean}
 */
export function appExists(slug) {
  return slug in APPS;
}

// Export the apps object for direct access if needed
export { APPS };
